DynamicControlInView.Script:

jQuery(document).ready(function(){
var dates = jQuery("#DataDe, #DataAte").datepicker({
    defaultDate: "+1w",
    changeMonth: true,
    numberOfMonths: 2,
    onSelect: function( selectedDate ) {
      var option = this.id == "DataDe" ? "minDate" : "maxDate",
      instance = jQuery( this ).data( "datepicker" ),
      date = jQuery.datepicker.parseDate(instance.settings.dateFormat || jQuery.datepicker._defaults.dateFormat, selectedDate, instance.settings );
      dates.not( this ).datepicker( "option", option, date );
}
});
});


jQuery(document).ready(function(){
var dates = jQuery( "#DataDe, #DataAte" ).datepicker({
defaultDate: "+1w",
changeMonth: true,
numberOfMonths: 2,
onSelect: function( selectedDate ) {
var option = this.id == "DataDe" ? "minDate" : "maxDate",
instance = jQuery( this ).data( "datepicker" ),
date = jQuery.datepicker.parseDate(
instance.settings.dateFormat ||
jQuery.datepicker._defaults.dateFormat,
selectedDate, instance.settings );
dates.not( this ).datepicker( "option", option, date );
}
});
});


jQuery(document).ready(function(){
  jQuery.ajax({
              url: '../../Funcionario/RecuperarFuncionarios',
              type: 'Post',
              dataType: "json",
              success: function (data) {
                  var gender = data;
                  if (gender.length > 0) {
                      var listItems = [];
                      listItems.push('<option selected value="">Todos</option>');
                      for (var i = 0; i < gender.length; i++) {
                          listItems.push('<option value="' + gender[i].Value + '">' + gender[i].Text + '</option>');
                      }
                      jQuery('#Funcionario').children().remove();
                      jQuery('#Funcionario').removeAttr('disabled');
                      jQuery('#Funcionario').append(listItems.join(''));
                  }
                  else {
                      alert("Nenhum Funcionário Cadastrado");
                  }
              }
              })
});


              jQuery(document).ready(function(){
              var dates = jQuery("#DATADE, #DATAATE").datepicker({
                  defaultDate: "+1w",
                  changeMonth: true,
				          minDate: "11/08/2011",
				          maxDate: "+0D",
                  numberOfMonths: 1,
                  onSelect: function( selectedDate ) {
                    var option = this.id == "DATADE" ? "minDate" : "maxDate",
                    instance = jQuery( this ).data( "datepicker" ),
                    date = jQuery.datepicker.parseDate(instance.settings.dateFormat || jQuery.datepicker._defaults.dateFormat, selectedDate, instance.settings );
                    dates.not( this ).datepicker( "option", option, date );
              }
              });
              });


jQuery('.CampoNumeric').live('keyup', function(e) {
  jQuery(this).val(jQuery(this).val().replace(/[^0-9,]/g, ''));
});

jQuery('.CampoNumeric').live('keypress', function(e){
  jQuery(this).val(jQuery(this).val().replace(/[^0-9,]/g, ''));
});


jQuery(":submit").click(function() {
  if (jQuery('#TIPO_TAXA').val() != '')
  {
    if (jQuery('#VALOR_TAXA_DE').val() == '' || jQuery('#VALOR_TAXA_ATE').val() == ''){
      alert('Valor das taxas inválidas.');
      return false;
    }
  }
});


