jQuery(document).ready(function(){
  jQuery.ajax({
    url: '/DynamicViews/RecuperarFiltros',
    context: document.body,
    dataType: 'json',
    type: 'Post',
    data: {
          q: jQuery('#Id').val()
    },
    success: function(data) {
      jQuery.each(data, function(Id, Nome){
        jQuery('#COMBO_FILTRO').append(jQuery("<option></option>").val(Nome.Id).html(Nome.Nome));
      });
    },
    error: function() { alert("Ocorreu um erro ao carregar os filtros."); }
  });

  jQuery('#COMBO_FILTRO').change(function() {
      jQuery.ajax({
        url: '/DynamicViews/RecuperarItensFiltro',
        context: document.body,
        dataType: 'json',
        type: 'Post',
        async: false,
        data: {
              q: jQuery(this).val()
        },
        success: function(data) {
          jQuery.each(data, function(Key, Value){
            jQuery('#' + Value.Key).val(Value.Value);
            if(Value.Key == 'camposSelecionados'){
              AlterarCamposSelecionados();
            }

          });
       }
      });
  });

    jQuery(":submit").click(                  
      function() {
      jQuery.ajax({
        url: '/DynamicViews/InserirFiltro',
        context: document.body,
        dataType: 'json',
        type: 'Post',
        data: jQuery(":input"),
        success: function(data) {
          jQuery.each(data, function(Key, Value){
            jQuery('#' + Value.Key).val(Value.Value);
          });
       }
      });
    });



});


jQuery(function () {
    jQuery("ul.droptrue").sortable({
        connectWith: "ul"
    });

    jQuery("ul.dropfalse").sortable({
        connectWith: "ul"
    });

    jQuery("#sortable1, #sortable2").disableSelection();
});

jQuery("#sortable2").bind("sortupdate", function (event, ui) {
    var result = jQuery('#sortable2').sortable('toArray');
    //camposSelecionados.value = result;
    jQuery("#camposSelecionados").val(result);
});

function marcaTodas() {
    var colunas = jQuery('#sortable1').sortable('toArray');
    //jQuery("#camposSelecionados").val( jQuery('#sortable1').sortable('toArray').toLocaleString() );

    jQuery.each(colunas, function (item) {
        jQuery('#sortable2').append(jQuery('#' + colunas[item]));
    });
    jQuery("#camposSelecionados").val( jQuery('#sortable2').sortable('toArray') )
}

function desmarcaTodas() {
    var colunas = jQuery('#sortable2').sortable('toArray');

    jQuery("#camposSelecionados").val('')

    jQuery.each(colunas, function (item) {
        jQuery('#sortable1').append(jQuery('#' + colunas[item]));
    });
    //camposSelecionados.value = todas;
}

jQuery("#sortable2").bind("sortcreate", function (event, ui) {
  jQuery('#camposSelecionados').val(jQuery('#sortable2').sortable('toArray'));
});



function AlterarCamposSelecionados() {
  var result = jQuery('#sortable2').sortable('toArray');

  for (var i in result) {
      jQuery('#sortable1').append(jQuery('#' + result[i]));
  }

  var colunasSalvadas = jQuery('#camposSelecionados').val().toLocaleString();
  var colunas = colunasSalvadas.replace(';',',').split(',');

  jQuery.each(colunas, function (coluna) {
      jQuery('#sortable2').append(jQuery('#' + colunas[coluna]));
  });


}


